mapper.select:
SELECT *
FROM state_machine_transform
WHERE organization_id = #{organizationId}
and id = #{id}

mapper.delete:
DELETE FROM state_machine_transform
WHERE id in (
  select t.id
  from state_machine_transform t
  where t.end_node_id not in (
    select n.id from state_machine_node n
  )
  and t.organization_id = #{organizationId}
)
