mapper.select:
SELECT *
FROM state
WHERE organization_id = #{organizationId}
and id = #{id}

SELECT * FROM state
WHERE 1=1

    AND organization_id = #{state.organizationId}


    AND name LIKE concat('%',#{state.name},'%')


    AND type LIKE concat('%',#{state.type},'%')


    AND description = #{state.description}


    AND ( name LIKE concat('%',#{param},'%')
    OR description LIKE concat('%',#{param},'%')
    )
